/**
* @param {String} Sub
*/
//**********************************************************************************************
function FindNormalCall(Sub)
//**********************************************************************************************
{
    // Find call in a normal calls
    var Index = -1;
    var found = false;

    if (call_leg_list.length > 0)
    {
        var i = 0;   

        while ((!found) && (i < call_leg_list.length))
        {
            var txt = "List.From: " + call_leg_list[i].FromDirNo +
                      ". List.To: " + call_leg_list[i].ToDirNo +
                      ". A_sub: " + Sub;
            DebugMessage(txt);          

            if ((call_leg_list[i].CallType == "normal_call") &&
                (call_leg_list[i].DirNo == Sub))
            {
                found = true;
                Index = i;
            }
            i++;
        }
    }
    if (found)
    {
        var txt = "FindNormalCall - Index: " + Index.toString();
        DebugMessage(txt);
    }
    else
    {
        var txt = "FindNormalCall - Not found.";
        DebugMessage(txt);
    }

    return Index;
}


