/**
* @param {String} asub (called sub)
* @param {String} bsub (calling sub)
*/
function FindQueuedCall(asub,bsub)
//**********************************************************************************************
{
    // Find call in a queued calls
    // bsub is call into a queue which is operated by asub
    var Index = -1;
    var found = false;

    if (call_leg_list.length > 0)
    {
        var i = 0;

        while ((!found) && (i < call_leg_list.length))
        {
            var txt = "FindQueuedCall. List[" + i.toString() + "].FromDirNo: " + call_leg_list[i].FromDirNo +
                ". List[" + i.toString() + "].ToDirNo: " + call_leg_list[i].ToDirNo +
                ". A_sub: " + asub;

            DebugMessage(txt);

            if ((call_leg_list[i].CallType == "queue_call") &&
                (call_leg_list[i].DirNo == asub) &&
                (call_leg_list[i].FromDirNo == bsub))
            {
                found = true;
                Index = i;
            }
            i++;
        }
    }
    if (found)
    {
        var txt = "FindQueuedCall - Index: " + Index.toString();
        DebugMessage(txt);
    }
    else
    {
        var txt = "FindQueuedCall - Not found.";
        DebugMessage(txt);
    }

    return Index;
}


/**
* @param {String} Sub
*/
//**********************************************************************************************
function StationIsFree(Sub)
//**********************************************************************************************
{
    var IsFree = true;

    if (call_leg_list.length > 0)
    {
        var i = 0;

        while ((IsFree) && (i < call_leg_list.length ))
        {
             var txt = "StationIsFree. List[" + i.toString() + "].FromDirNo: " + call_leg_list[i].FromDirNo +
                ". List[" + i.toString() + "].ToDirNo: " + call_leg_list[i].ToDirNo +
                ". A_sub: " + Sub;

            DebugMessage(txt);


            if (call_leg_list[i].FromDirNo == Sub)
            {
                IsFree = false;
            }
            else
            {
                if (call_leg_list[i].ToDirNo == Sub)
                {
                    IsFree = false;
                }
            }
            i++;
        } 
    }
    var txt = "StationIsFree. Return: " + IsFree.toString();
    DebugMessage(txt);

    return IsFree;
}


/**
* @param {String} asub
* @param {String} bsub
*/
//**********************************************************************************************
function StationsAreConnected(asub, bsub)
//**********************************************************************************************
{
    var AreConnected = false;

    if (call_leg_list.length > 0)
    {
        var i = 0;

        while ((!AreConnected) && (i < call_leg_list.length))
        {
            var fromDir = call_leg_list[i].FromDirNo;
            var toDir = call_leg_list[i].ToDirNo;
            var state = call_leg_list[i].State;

            if (state == "in_call")
            {
                if ( ((fromDir == asub) && (toDir == bsub)) ||
                     ((fromDir == bsub) && (toDir == asub)) )
                {
                    AreConnected = true;
                }
            }

            i++;
        }
    }

    var txt = "StationsAreConnected. Return: " + AreConnected.toString();
    DebugMessage(txt);

    return AreConnected;
}


