        var priority  = msg.payload[i]["priority"];
        var reason    = msg.payload[i]["reason"];
        var state     = msg.payload[i]["state"];
        var toDirNo   = msg.payload[i]["to_dirno"];

        txt = "On Call Leg Status Event" +
              ". call_id: "    + callId + 
              ". call_type: "  + callType +
              ". channel: "    + channel +
              ". dirno: "      + dirNo +
              ". from_dirno: " + fromDirNo +
